function calculate(){this.loadInput(),this.billAmount&&this.tipPercent?(this.calculateTipTotal(),$("#tipAmountText").val(this.tipAmount.toFixed(2).toString()),$("#totalText").val(this.totalAmount.toFixed(2).toString())):($("#tipAmountText").val("--"),$("#totalText").val("--"))}function loadInput(){this.billAmount=parseFloat($("#billAmountText").val()),this.tipPercent=parseFloat($("#tipPercentText").val())}function calculateTipTotal(){this.tipAmount=this.billAmount*.01*this.tipPercent,this.totalAmount=this.billAmount+this.tipAmount}var billAmount,tipPercent,tipAmount,totalAmount